import React, { useState } from "react";

export default function MBIGSMobileFriendly() {
  const [score, setScore] = useState(null);

  const handleSubmit = () => {
    let total = 0;
    for (let i = 0; i < 16; i++) {
      const selected = document.querySelector(`input[name='q${i}']:checked`);
      if (selected) {
        total += parseInt(selected.value, 10);
      }
    }
    setScore(total);
  };

  const renderResult = () => {
    if (score === null) return null;
    let message = "";
    if (score >= 60) {
      message = "⚠️ 높은 번아웃 수준이 감지되었습니다. 휴식과 상담을 고려해보세요.";
    } else if (score >= 40) {
      message = "⚠️ 중간 수준의 번아웃 신호가 있습니다. 자기관리에 주의가 필요합니다.";
    } else {
      message = "😊 번아웃 수준이 낮습니다. 현재 상태를 잘 유지하세요!";
    }
    return (
      <div style={{ marginTop: "1rem", textAlign: "center", background: "#eef2ff", padding: "1rem", borderRadius: "8px" }}>
        <p style={{ fontWeight: "bold" }}>총점: {score}점</p>
        <p>{message}</p>
      </div>
    );
  };

  const labels = [
    "전혀없다",
    "1년에 2-3회 또는 그 미만",
    "한 달에 한 번 또는 그 미만",
    "한 달에 2-3회",
    "일주일에 1회 정도",
    "일주일에 2-3회",
    "매일"
  ];

  return (
    <div style={{ padding: "1rem", fontFamily: "sans-serif" }}>
      <h2>📋 설문지</h2>
      <p>다음 질문에 0에서 6 사이의 점수로 답해주세요.</p>
      <table style={{ width: "100%", fontSize: "0.9rem", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th>#</th>
            <th style={{ textAlign: "left" }}>질문지</th>
            {labels.map((label, i) => (
              <th key={i} style={{ fontSize: "10px", padding: "4px" }}>{i}<br />{label}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {[
            "맡은 일을 하는데 있어서 정서적으로 지쳐있음을 느낀다.",
            "직장 일을 마치고 퇴근할 때 완전히 지쳐있음을 느낀다.",
            "아침에 일어나서 출근할 생각만 하면 피곤함을 느낀다.",
            "하루 종일 일하는 것은 나를 긴장 시킨다.",
            "나는 직무상 발생하는 문제들을 효과적으로 해결할 수 있다.",
            "맡은 일을 수행하는데 있어서 완전히 지쳐있다.",
            "현재 소속된 직장에 효과적인 기여를 하고 있다고 느낀다.",
            "현재의 업무를 시작한 이후로 내 일에 대한 관심이 줄었다.",
            "맡은 일을 하는데 있어서 소극적이 되었다.",
            "내가 생각할 때, 나는 일을 잘 한다.",
            "직무상 무언가를 성취했을 때 기쁨을 느낀다.",
            "나는 현재의 직무에서 가치 있는 많은 것을 이루어왔다.",
            "나는 방해받지 않고 내 일을 수행하기 원할 뿐이다.",
            "내 업무가 무언가에 기여하든 말든 나는 점점 더 냉소적이 되었다.",
            "내 직무의 중요성이 의심스럽다.",
            "직무상, 나는 일을 효과적으로 처리하고 있다는 자신감이 있다."
          ].map((q, idx) => (
            <tr key={idx}>
              <td>{idx + 1}</td>
              <td style={{ maxWidth: "18rem", wordBreak: "break-word", textAlign: "left" }}>{q}</td>
              {labels.map((_, i) => (
                <td key={i}><input type="radio" name={`q${idx}`} value={i} style={{ width: "16px", height: "16px" }} /></td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      <div style={{ textAlign: "center", marginTop: "1rem" }}>
        <button onClick={handleSubmit} style={{ background: "black", color: "white", padding: "0.5rem 1rem", borderRadius: "6px" }}>결과 보기</button>
        {renderResult()}
      </div>
    </div>
  );
}
