import React, { useState } from "react";
import "./index.css";

export default function BurnoutSurvey() {
  const [scoreSummary, setScoreSummary] = useState(null);

  const labels = [
    "전혀없다",
    "1년에 2-3회 또는 그 미만",
    "한 달에 한 번 또는 그 미만",
    "한 달에 2-3회",
    "일주일에 1회 정도",
    "일주일에 2-3회",
    "매일"
  ];

  const questions = [
    "맡은 일을 하는 데 있어서 정서적으로 고갈된 느낌이 든다.",
    "직장 일을 마치고 퇴근할 때쯤이면 기진맥진한 느낌이 든다.",
    "아침에 일어나서 다시 출근할 생각을 하면 피곤한 느낌이 든다.",
    "학생들의 감정을 쉽게 이해할 수 있다.",
    "일부 학생들에 대해서 인격이 없는 물체처럼 대하고 있다고 느낀다.",
    "하루 종일 사람들과 함께 일한다는 것은 힘든 일이다.",
    "학생들의 문제를 효과적으로 해결한다.",
    "일 때문에 소진된 상태이다.",
    "일을 통해 다른 사람들의 삶에 긍정적인 영향을 주고 있다고 느낀다",
    "선생님이 된 이후로 사람들에게 점점 둔감해졌다.",
    "이 직업으로 인해 내가 정서적으로 메말라가는 것 같아 걱정이다.",
    "매우 활기찬 느낌이 든다.",
    "업무로 인해 좌절감을 느낀다.",
    "주어진 일을 지나치게 열심히 한다고 느낀다.",
    "어떤 학생들에게는 무슨 일이 일어났는지 별로 신경 쓰지 않는다.",
    "사람들과 직접 대하면서 일하는 것에 스트레스를 받는다.",
    "학생들과 쉽게 편안한 분위기를 만들 수 있다.",
    "학생들과 친밀하게 일하고 나면 흐뭇해진다.", 
    "직업을 통해 가치 있는 많은 것들을 성취해왔다.",
    "한계에 다다른 느낌이 든다.", 
    "직장에서 감정적인 문제들을 매우 침착하게 다룬다.", 
    "학생들이 어떨 때는 자신들의 문제들에 대해 나를 비난하고 있다고 느낀다."
  ];

  const handleSubmit = () => {
    const getValue = (i) => {
      const selected = document.querySelector(`input[name='q${i}']:checked`);
      return selected ? parseInt(selected.value, 10) : 0;
    };

    const exhaustionIdx = [0, 1, 2, 5, 7, 12, 13, 15, 19];
    const depersonalIdx = [4, 9, 10, 14, 21];
    const efficacyIdx = [3, 6, 8, 11, 16, 17, 18, 20];

    const exhaustion = exhaustionIdx.reduce((sum, i) => sum + getValue(i), 0) / exhaustionIdx.length;
    const depersonal = depersonalIdx.reduce((sum, i) => sum + getValue(i), 0) / depersonalIdx.length;
    const efficacy = efficacyIdx.reduce((sum, i) => sum + getValue(i), 0) / efficacyIdx.length;

    const exhaustionT = 50 + 10 * (exhaustion - 21.25) / 11.01;
    const depersonalT = 50 + 10 * (depersonal - 11.0) / 6.19;
    const efficacyT = 50 + 10 * (efficacy - 33.54) / 6.89;

    setScoreSummary({ exhaustion, depersonal, efficacy, exhaustionT, depersonalT, efficacyT });
  };

  const renderResult = () => {
    if (!scoreSummary) return null;
    const { exhaustion, depersonal, efficacy, exhaustionT, depersonalT, efficacyT } = scoreSummary;
    return (
      <div className="mt-6 text-center bg-indigo-50 border border-indigo-200 p-4 rounded-md">
        <p className="font-semibold">소진 평균: {exhaustion.toFixed(2)} / T 점수: {exhaustionT.toFixed(1)}</p>
        <p className="font-semibold">비인격화 평균: {depersonal.toFixed(2)} / T 점수: {depersonalT.toFixed(1)}</p>
        <p className="font-semibold">효능감 평균: {efficacy.toFixed(2)} / T 점수: {efficacyT.toFixed(1)}</p>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-white py-6 font-sans text-gray-800">
      <div className="max-w-3xl mx-auto px-4">
        <main className="space-y-6">
          <section className="bg-gray-100 rounded-xl shadow p-4">
            <h2 className="text-lg font-semibold mb-2">📋 설문지</h2>
            <p className="text-sm mb-4">다음 질문에 0에서 6 사이의 점수로 답해주세요.<br />(0: 전혀없다, 1: 1년에 2-3회 또는 그 미만, 2: 한 달에 한 번 또는 그 미만, 3: 한 달에 2-3회, 4: 일주일에 1회 정도, 5: 일주일에 2-3회, 6: 매일)</p>
            <div className="overflow-auto">
              <table className="min-w-full text-xs border border-gray-300">
                <thead className="bg-gray-200">
                  <tr>
                    <th className="border px-2 py-1">#</th>
                    <th className="border px-2 py-1 text-left">질문지</th>
                    {labels.map((label, i) => (
                      <th key={i} className="border px-1 text-center text-[10px] leading-3 w-20">
                        {i}<br />{label}
                      </th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {questions.map((q, idx) => (
                    <tr key={idx} className="even:bg-gray-50">
                      <td className="border px-2 py-1 text-center">{idx + 1}</td>
                      <td className="border px-2 py-1 align-top max-w-[16rem] sm:max-w-[24rem] whitespace-pre-wrap break-words">{q}</td>
                      {labels.map((_, i) => (
                        <td key={i} className="border px-1 py-1 text-center">
                          <input type="radio" name={`q${idx}`} value={i} className="w-4 h-4 mx-auto" />
                        </td>
                      ))}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            <div className="flex justify-center mt-4">
              <button onClick={handleSubmit} className="bg-black text-white px-4 py-2 rounded-md">결과 보기</button>
            </div>
            {renderResult()}
          </section>
        </main>
        <footer className="text-center mt-10 text-xs text-gray-500">
          &copy; 2025 yeunkim. All rights reserved.
        </footer>
      </div>
    </div>
  );
}
